@output_table =[]

def populate_shipping method, zones, shipping_rates, country = 'USA', non_pounds = false, maximum = false
	puts "Adding #{method}..."
	zones.each do |x|
		max = maximum ?  x[2] : 10000
		if country == 'USA'
			start_zone = x[0]
	#		puts start_zone
			end_zone = x[1]
	#		puts end_zone
			ship_zone = x[2]
	#		puts ship_zone
		else
			country = x[0]
			start_zone = "*"
			end_zone = "*"
			ship_zone = x[1]
		end
		shipping_rates_position = shipping_rates[0].index(ship_zone)
		shipping_rates.each_index do |y|
			if y != 0
				if non_pounds
					start_weight = y == 1 ? 0 : shipping_rates[y-1][0]
					end_weight = shipping_rates[y][0].to_f - 0.0001
				else
					start_weight = y - 1
					end_weight = y.to_f - 0.0001
				end
				if method.eql? 'DHL Express'
					rate = shipping_rates[y][shipping_rates_position] + 15.0
				else
					rate = shipping_rates[y][shipping_rates_position]
				end
		# country,region,city,start zipe, end zip, start weight, end weight, shipping price, delivery Type
				output_string = "#{country},*,*,#{start_zone},#{end_zone},#{start_weight.to_s},#{end_weight.to_s},#{rate.to_s},#{method}" unless y > max
				@output_table.push(output_string) unless y > max
			end
		end
	end
end

def choose_methods store_name, fc_international = true, ups_ground = true, usps_ground = true, usps_priority = true, usps_first = true, dhl = true, usps_priority_i = true, ups_canada = true
	puts "Processing #{store_name}..."

	# finished ups discounts

	#Kyberlight
	populate_shipping('USPS FC International', @usps_first_i_postal_codes, @usps_first_i_shipping_rates, 'International', true) if fc_international
	#call UPS ground method.
	populate_shipping('UPS Ground (4-5 Business Days)', @ups_ground_postal_codes, @ups_ground_shipping_rates) if ups_ground
	#Call Shipping Method for USPS Ground
	populate_shipping('USPS Ground(2-8 Business Days)', @usps_postal_codes, @usps_ground_shipping_rates) if usps_ground
	#Call Shipping Method for USPS Priority
	populate_shipping('USPS Priority(3-5 Business Days)', @usps_postal_codes, @usps_commercial_plus_shipping_rates) if usps_priority
	#Call Shipping Method for USPS First Class
	populate_shipping('USPS First Class(2-8 Business Days)', @first_postal_codes, @first_shipping_rates, 'USA', true) if usps_first
	#call method to pouplate DHL data
	populate_shipping('DHL Express', @dhl_postal_codes, @dhl_shipping_rates, 'International') if dhl
	#usps priority international shipping call
	populate_shipping('USPS Priority International', @usps_i_postal_codes, @usps_first_priority_shipping_rates, 'International', false, true) if usps_priority_i
	#ups canada
	populate_shipping('UPS Standard', @ups_i_postal_codes, @ups_shipping_rates, 'International', false, false) if ups_canada
puts "Saving #{store_name}..."
	File.new("matrix_rates_#{store_name}.csv", "w+")
	File.open("matrix_rates_#{store_name}.csv", "w+") do |x|
		@output_table.each {|e| x.puts(e)}
	end
	@output_table = []
	puts "Saved #{store_name}"
end

